mapper.select:
SELECT *
FROM CATEGORY
WHERE 1=1

    AND STATE = #{state}

ORDER BY SEQ

    LIMIT #{start}, #{page_block}

SELECT C.CATEGORY, C.NAME, C.SEQ, C.STATE ,C.ICON, CCG.GROUP AS CCG_GROUP, CC.CLASSIFICATION AS CC_CLASSIFICATION, CC.NAME AS CC_NAME, CCG.SEQ AS CC_SEQ, CCG.STATE AS CC_STATE, CCG.CNT AS CC_CNT, CK.KEYWORD AS CK_KEYWORD, CK.NAME AS CK_NAME FROM CATEGORY C LEFT JOIN CATEGORY_CLASSIFICATION_GROUP CCG on CCG.CATEGORY = C.CATEGORY LEFT JOIN CATEGORY_CLASSIFICATION CC on CCG.CLASSIFICATION = CC.CLASSIFICATION LEFT JOIN CATEGORY_KEYWORD CK on CC.CLASSIFICATION = CK.CLASSIFICATION WHERE C.CATEGORY = #{category}
AND C.STATE = #{state} AND IFNULL(CCG.STATE,1) = #{state}
ORDER BY C.SEQ, CCG.SEQ, CK.KEYWORD

mapper.update:
UPDATE CATEGORY_CLASSIFICATION_GROUP SET
    CLASSIFICATION = #{classification}
    ,SEQ = #{seq}
    ,STATE = #{state}
    ,CNT = #{cnt}
    ,UPDDATE = NOW()
WHERE 1=1
AND `GROUP` = #{group}

mapper.delete:
DELETE FROM CATEGORY_CLASSIFICATION_GROUP WHERE CATEGORY = #{category} AND CLASSIFICATION IN (

    #{classification.classification}

)

mapper.update:
UPDATE CATEGORY SET
    NAME = #{name}
    ,SEQ = #{seq}
    ,STATE = #{state}
    ,ICON = #{icon}
    ,UPDDATE = NOW()
WHERE 1=1
    AND CATEGORY = #{category}

mapper.delete:
DELETE FROM CATEGORY_CLASSIFICATION WHERE CLASSIFICATION IN (

    #{classification.classification}

)

DELETE FROM CATEGORY_KEYWORD WHERE KEYWORD IN (

    #{keyword.keyword}

)
