mapper.select:
SELECT * FROM MEMBER WHERE 1=1
ORDER BY MEMBER DESC
LIMIT #{start}, #{page_block}

mapper.update:
UPDATE MEMBER SET
    PHONE = #{phone}
    ,NICKNAME = #{nickname}
    ,JOB = #{job}
    ,MONEY = #{money}
    ,LOCATION = #{location}
    ,KEYWORDS = #{keywords}
    ,UPDDATE = now()
,PASSWORD = #{password}
WHERE MEMBER = #{member}
