mapper.update:
UPDATE MEMBER SET
    NICKNAME = #{nickname}
,PASSWORD = #{password}
,PHONE = #{phone}
,PROFILE = #{profile}
    ,UPDDATE = now()
WHERE MEMBER = #{member}

mapper.select:
SELECT
    GET_MEMBER_KEYWORD(MEMBER) AS KEYWORDS, MEMBER.*
FROM MEMBER
WHERE 1=1
AND EMAIL = #{email}
AND PHONE = #{phone}
AND NICKNAME = #{nickname}

SELECT *
FROM MEMBER
WHERE 1=1

    AND EMAIL = #{email}


    AND NAME = #{name}


    AND BIRTHDAY = #{birthday}
