mapper.select:
SELECT P.* ,(SELECT NICKNAME FROM MEMBER WHERE MEMBER = P.MEMBER) AS NICKNAME ,(SELECT ACCOUNT FROM MEMBER WHERE MEMBER = P.MEMBER) AS ACCOUNT FROM POINT P LEFT JOIN MEMBER M on P.MEMBER = M.MEMBER WHERE 1=1
ORDER BY POINT DESC
LIMIT #{start}, #{page_block}

mapper.update:
UPDATE POINT SET
    STATE = #{state}
    ,UPDDATE = now()
WHERE POINT = #{point}
