mapper.select:
SELECT P.*, M.NICKNAME, M.ACCOUNT, M.NAME FROM POINT P LEFT JOIN MEMBER M on P.MEMBER = M.MEMBER WHERE 1=1
ORDER BY POINT DESC
LIMIT #{start}, #{page_block}

mapper.update:
UPDATE POINT SET
    STATE = #{state}
    ,UPDDATE = now()

    ,ACCUMULATE = #{accumulate}

WHERE POINT = #{point}
