mapper.update:
UPDATE QUESTION SET
    HITS = HITS + 1
WHERE QUESTION = #{question}

UPDATE QUESTION SET
    TYPE = #{type}
    ,TITLE = #{title}
    ,CONTENT = #{content}
    ,POINT = #{point}
    ,STATE = #{state}
    ,KEYWORDS = #{keywords}
WHERE QUESTION = #{question}

mapper.select:
SELECT *
FROM QUESTION_ANSWER
WHERE 1=1
    AND QUESTION = #{question}
ORDER BY ANSWER DESC

    LIMIT #{start}, #{page_block}

mapper.update:
UPDATE QUESTION_ANSWER SET
CHOOSE = !CHOOSE
WHERE ANSWER = #{answer}
