mapper.update:
UPDATE QUESTION SET
    HITS = HITS + 1
WHERE QUESTION = #{question}

UPDATE QUESTION SET
    TYPE = #{type}
    ,TITLE = #{title}
    ,CONTENT = #{content}
    ,NOKEYWORD = #{nokeyword}
    ,POINT = #{point}
    ,STATE = #{state}
    ,KEYWORDS = #{keywords}
    ,UPDDATE = now()
WHERE QUESTION = #{question}

mapper.select:
SELECT QA.* , M.MEMBER AS MEMBER_MEMBER , M.PROFILE AS MEMBER_PROFILE , M.NAME AS MEMBER_NAME , M.NICKNAME AS MEMBER_NICKNAME , M.KEYWORDS AS MEMBER_KEYWORDS FROM QUESTION_ANSWER QA LEFT JOIN MEMBER M on QA.MEMBER = M.MEMBER WHERE 1=1 AND QA.QUESTION = #{question}
AND QA.MEMBER = #{answerMember}
ORDER BY QA.ANSWER DESC
LIMIT #{start}, #{page_block}
