mapper.select:
SELECT R.*
,(SELECT COUNT(*) FROM REVIEW_LIKE RL WHERE RL.REVIEW = R.REVIEW AND RL.MEMBER = #{member} AND DEL = 0) LIKE_YN
,(SELECT COUNT(*) FROM REVIEW_LIKE RL WHERE RL.REVIEW = R.REVIEW AND DEL = 0) LIKE_TOT ,(SELECT COUNT(*) FROM REVIEW_REPLY RR WHERE RR.REVIEW = R.REVIEW) REPLY_TOT ,(SELECT NAME FROM CATEGORY C WHERE C.CATEGORY = R.CATEGORY) CATEGORY_NAME ,M.NICKNAME MEMBER_NICKNAME FROM REVIEW R LEFT JOIN MEMBER M on R.MEMBER = M.MEMBER WHERE 1=1 AND R.REVIEW = #{review}

mapper.update:
UPDATE REVIEW SET
    HITS = HITS + 1
WHERE REVIEW = #{review}

mapper.insert:
INSERT INTO REVIEW (
    MEMBER, CATEGORY, BRAND, NAME, MNUMBER, PHOTO, VIDEO,
    GRADE, MOTIVE, PLUS, MINUS,
    PROPOSER, STORE, PRICE, LINK, USETIME, TIP, KEYWORDS
) VALUES (
    #{member}, #{category}, #{brand}, #{name}, #{mnumber}, #{photo}, #{video},
    #{grade}, #{motive}, #{plus}, #{minus},
    #{proposer}, #{store}, #{price}, #{link}, #{usetime}, #{tip}, #{keywords}
)

mapper.update:
UPDATE REVIEW SET
    CATEGORY = #{category}
    ,BRAND = #{brand}
    ,NAME = #{name}
    ,MNUMBER = #{mnumber}
    ,PHOTO = #{photo}
    ,VIDEO = #{video}
    ,GRADE = #{grade}
    ,MOTIVE = #{motive}
    ,PLUS = #{plus}
    ,MINUS = #{minus}
    ,PROPOSER = #{proposer}
    ,STORE = #{store}
    ,PRICE = #{price}
    ,LINK = #{link}
    ,USETIME = #{usetime}
    ,TIP = #{tip}
    ,KEYWORDS = #{keywords}
    ,UPDDATE = now()
WHERE REVIEW = #{review}

mapper.insert:
INSERT INTO REVIEW_REPLY (
REVIEW, MEMBER, CONTENT
) VALUES (
#{review}, #{member}, #{content}
)
