mapper.select:
SELECT GET_REVIEW_KEYWORD(R.REVIEW, R.MEMBER) AS KEYWORDS, R.*
,(SELECT COUNT(*) FROM REVIEW_LIKE RL WHERE RL.REVIEW = R.REVIEW AND RL.MEMBER = #{member} AND DEL = 0) LIKE_YN
,(SELECT COUNT(*) FROM REVIEW_LIKE RL WHERE RL.REVIEW = R.REVIEW AND DEL = 0) LIKE_TOT ,(SELECT COUNT(*) FROM REVIEW_REPLY RR WHERE RR.REVIEW = R.REVIEW) REPLY_TOT ,(SELECT NAME FROM CATEGORY C WHERE C.CATEGORY = R.CATEGORY) CATEGORY_NAME ,(SELECT COUNT(*) FROM REPORT WHERE `KEY` = R.REVIEW AND TYPE = '1') REPORT_CNT ,(SELECT COUNT(*) FROM INFO WHERE TYPE = 1 AND `KEY` = R.REVIEW) MODI_CNT ,M.PROFILE MEMBER_PROFILE ,M.NAME MEMBER_NAME ,M.NICKNAME MEMBER_NICKNAME ,GET_MEMBER_KEYWORD(M.MEMBER) AS MEMBER_KEYWORDS FROM REVIEW R LEFT JOIN MEMBER M on R.MEMBER = M.MEMBER WHERE 1=1 AND R.REVIEW = #{review}

mapper.update:
UPDATE REVIEW SET
    HITS = HITS + 1
WHERE REVIEW = #{review}

mapper.insert:
INSERT INTO REVIEW (
    MEMBER, CATEGORY, BRAND, NAME, MNUMBER, PHOTO, VIDEO,
    GRADE, MOTIVE, PLUS, MINUS,
    PROPOSER, STORE, PRICE, LINK, USETIME, TIP, KEYWORDS
) VALUES (
    #{member}, #{category}, #{brand}, #{name}, #{mnumber}, #{photo}, #{video},
    #{grade}, #{motive}, #{plus}, #{minus},
    #{proposer}, #{store}, #{price}, #{link}, #{usetime}, #{tip}, #{keywords}
)

mapper.update:
UPDATE REVIEW SET
    CATEGORY = #{category}
    ,BRAND = #{brand}
    ,NAME = #{name}
    ,MNUMBER = #{mnumber}
    ,PHOTO = #{photo}
    ,VIDEO = #{video}
    ,GRADE = #{grade}
    ,MOTIVE = #{motive}
    ,PLUS = #{plus}
    ,MINUS = #{minus}
    ,PROPOSER = #{proposer}
    ,STORE = #{store}
    ,PRICE = #{price}
    ,LINK = #{link}
    ,USETIME = #{usetime}
    ,TIP = #{tip}
    ,KEYWORDS = #{keywords}
    ,STATE = #{state}
    ,UPDDATE = now()
WHERE REVIEW = #{review}

mapper.insert:
INSERT INTO REVIEW_REPLY (
    REVIEW, MEMBER, CONTENT

    , REPLY_KEY

) VALUES (
    #{review}, #{member}, #{content}

    , #{reply_key}

)

mapper.select:
SELECT RR.*, M.MEMBER AS MEMBER_MEMBER, M.PROFILE AS MEMBER_PROFILE, M.NAME AS MEMBER_NAME, M.NICKNAME AS MEMBER_NICKNAME, GET_MEMBER_KEYWORD(M.MEMBER) AS MEMBER_KEYWORDS FROM REVIEW_REPLY RR LEFT JOIN MEMBER M on RR.MEMBER = M.MEMBER WHERE 1=1 AND RR.REVIEW = #{review}
AND RR.REPLY_KEY IS NULL
AND RR.REPLY_KEY = #{reply_key}
ORDER BY RR.REPLY DESC

SELECT COUNT(*)
FROM REVIEW_REPLY
WHERE 1=1
    AND REVIEW = #{review}

    AND REPLY_KEY IS NULL


    AND REPLY_KEY = #{reply_key}
